SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified qmt,q.top
,tag,q.creator,q.view_count,q.like_count,q.comment_count,q.category
,u.id uid,u.account_id account,u.name,u.token,u.rank,
u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company
FROM question q ,user u

    q.creator=u.id

        and category = #{category}


        and title regexp #{search}


        and tag regexp #{tag}


    ORDER by top Desc,qct DESC

SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified
qmt,tag,q.creator,q.view_count,q.like_count,q.comment_count,q.category,q.top
,u.id uid,u.account_id account,u.name,u.token,u.rank,
u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company
FROM question q ,user u

    q.creator=u.id

        and q.gmt_create between #{beginTime} and #{endTime}


        and tag regexp #{tag}


        and category = #{category}

    ORDER by top Desc, comment_count desc, view_count Desc ,like_count DESC,qct desc

SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified qmt,tag,q.creator,q.like_count,q.top,q.comment_count,q.view_count,q.category ,u.id uid,u.account_id account,u.name,u.token,u.rank, u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company FROM question q ,user u
q.creator=u.id
and tag regexp #{tag}
and category = #{category}
AND comment_count=0 order by top Desc, q.gmt_create desc

SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified qmt,tag,q.creator,q.like_count,q.top,q.comment_count,q.view_count,q.category ,u.id uid,u.account_id account,u.name,u.token,u.rank, u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company FROM question q ,user u
q.creator=u.id
and tag regexp #{tag}
and category = #{category}
and like_count !=0 order by top Desc, like_count desc , view_count desc,comment_count desc ,q.gmt_create desc

SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified qmt,tag,q.creator,q.like_count,q.top,q.comment_count,q.view_count,q.category ,u.id uid,u.account_id account,u.name,u.token,u.rank, u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company FROM question q ,user u
q.creator=u.id
and tag regexp #{tag}
and category = #{category}
and comment_count !=0 order by top Desc, comment_count desc , view_count desc,like_count desc,q.gmt_create desc

SELECT q.id qid, q.title,q.gmt_create qct ,q.gmt_modified qmt,tag,q.creator,q.like_count,q.comment_count,q.view_count,q.category,q.top ,u.id uid,u.account_id account,u.name,u.token,u.rank, u.gmt_create uct,u.gmt_modified umt,u.avatar_url,u.bio,u.location,u.company FROM question q ,user u
q.creator=u.id
and tag regexp #{tag}
and category = #{category}
and comment_count !=0 order by top Desc, view_count desc
